import React from 'react'
import { useState } from 'react';

function ReportCrime() {
  const [location, setName] = useState("");
  const handleSubmit = (event) => {
    event.preventDefault();
    alert(`The name you entered was: ${location}`);
  }
  return (
    <form onSubmit={handleSubmit}>
    <label>Specific location or address:
      <input type="text" 
          value={location}
          onChange={(e) => setName(e.target.value)}/>
    </label>
    <bk/>

    <label>Relevant Dates / Times:
      <input type="text" />
    </label>
    <label>Description of Incident:
      <input type="text" />
    </label>
    <label>Suspect's Name and Description:
      <input type="text" />
    </label>
    <bk/>

    <label>Vehicle Description (if applicable):
      <input type="text" />
    </label>
    <input type="submit" />
  </form>

  )
}

export default ReportCrime
