//     <form>
        
//         <div className = "field1">
//         <label> customer info </label>
//         <input placeholder="Name"/>        
//         <input placeholder="Phone 000-000-0000"/>
//         <input placeholder="E-mail"/>
//         <textarea placeholder="Shipping Address"/>
//         <textarea placeholder="Physical location of the project"/>
//         </div>

//         <button type = "submit" id= "submitBtn" className = "submitBtn"> submit</button>
//     </form>

//     <button className = "prevBtn" type = "submit"> PREV </button>
//     <button className = "nextBtn" type = "submit"> NEXT </button>

// </div>
    <div className='form-box'>
    <form onSubmit={handleSubmit} className="form">
    <div className = "field1">
      <select className="item" value={MyCar} onChange={handleChange}>
        <option value="Ford">Ford</option>
        <option value="Volvo">Volvo</option>
        <option value="Fiat">Fiat</option>
      </select>
    <label className="item"
    styles={{
      color:"red"
    }}>Enter your name:
    <textarea
      type="text" 
      name="username" 
      value={inputs.username || ""} 
      onChange={handleChange}
      
    />
    </label>
    <label>Enter your age:
    <textarea
        type="number" 
        name="age" 
        value={inputs.age || ""} 
        onChange={handleChange}
      />
      </label>
      <input type="submit" />
      </div>
  </form>
  </div>

  )
}

export default ReportCrime
